import React from "react";
import {StyleSheet, View} from "react-native";

const Card = props => {
    return (
        <View style={{...styles.card, ...props.style}}>
            {props.children}
        </View>
    )
};

const styles = StyleSheet.create({
    card: {
        padding: 10,
        backgroundColor: '#fee75c',
        borderRadius: 5,
        // iOS only
        shadowColor: '#5865f2',
        shadowOffset: {width: 0, height: 3},
        shadowRadius: 6,
        shadowOpacity: 0.3,
        // Android only
        elevation: 6,
    },
});

export default Card
